        DEVICE ZXSPECTRUM1024 ;Тексты диалогов;RUS=1        include "settings.ast";Формат диалога:;"TXT",0 - конец диалога;"TXT",1 - перевод на собеседника;"TXT",2 - перевод на гл. героя;"TXT",3,X - переход на другой диалог и конец текущ. диалога;"TXT",4,X - выполнение скрипта под номером X и конец диалога;"TXT",5,X - выполнение скрипта X, который возвращ. в HL адрес; след. диалога, при A'=0 героя, A'<>0 собеседника;Т.е. может служить для продолжения речи какого-либо героя;"TXT",13- перевод на строку ниже;длина строки 34 симв. в диалоге;КомандыEND     EQU 0SOBES   EQU 1HERO    EQU 2DIALOG  EQU 3SCRPT   EQU 4NEXT    EQU 6 ;продолжение диалога любого персонажа              ;нужно когда весь диалог не влезает в окно диалогаCR      EQU 13;перевод строкиPL      EQU #C000 ;реальное местоположение текстовFA      EQU #7000 ;куда компилируемSM=-FA+PL        ORG FA;Провидец ГлебGL_     DW G0+SM,G1+SM ;ссылки на диалог		if RUSG0      DB "Возьмите, может сгодится.",SOBES        DB "Вещи - это оковы, которые тянут",CR        DB "человека к земле...",END		elseG0      DB "Take this. It might be helpful.",SOBES        DB "Things are fetters that pull a man",CR        DB "to the ground...",END				endif 		if RUSG1      DB "Здравствуйте, молодой человек.",SOBES        DB "Приветствую вас, Вера!",HERO		elseG1      DB "Greetings, young man.",SOBES        DB "Greetings, Vera!",HERO				endif 		if RUS        DB "Вы, должно быть, Глеб?",SOBES        DB "Да, зовут меня Глеб. Хотя все",CR  ;запятая после "хотя" пропущена        DB "кличут Провидцем, потому как",CR        DB "считают, что я будущее вижу.",HERO		else        DB "You must be Gleb?",SOBES        DB "Yes. My name is Gleb, but everyone",CR        DB "calls me the Seer, because they",CR        DB "think I can see the future.",HERO				endif 		if RUS        DB "А на самом деле, не так что ли?",SOBES        DB "Нет, я вижу суть вещей!",HERO			; отрицание отрицания!		else        DB "Are they wrong?",SOBES        DB "Yes. I see the essence of things!",HERO				endif		if RUS        DB "Вроде мне это и надо. Загляни-ка",CR        DB "в эту самую ",34,"суть",34,", и скажи,",CR        DB "кто это тут безобразничает?",SOBES        DB "Так, где мой хрустальный шар...",CR        DB "Оммммм...",HERO		else        DB "Well, I need that. Take a look at",CR        DB "the ",34,"essence",34," and tell me who is",CR        DB "responsible for this mess.",SOBES        DB "OK, where is my crystal ball...?",CR        DB "Hmmmmm...",HERO		endif				if RUS        DB "Что ты видишь?",SOBES        DB "Вижу око!",HERO		else        DB "What do you see?",SOBES        DB "I see the eye!",HERO				endif		if RUS        DB "Что?",SOBES        DB "Тьфу ты! Не тот канал. Уммммм...",HERO		else        DB "What?",SOBES        DB "Ugh! Wrong channel. Ummmmm...",HERO				endif		if RUS        DB "...Удобная, наверное, штука...",SOBES        DB "Точно! Особенно в пятницу",CR        DB "вечером... Вот!",HERO		else        DB "This thing must be convenient.",SOBES        DB "Exactly! Especially on Friday",CR        DB "evenings... Here we are!",HERO		endif		if RUS        DB "Что?",SOBES        DB "Вижу колдуна... Имя... Зловет!",CR        DB "Железная палка в руке. Злой",CR        DB "наверно.",HERO		else	DB "What?",SOBES        DB "I see a sorcerer. Name of Evilon!",CR        DB "Iron staff in hand.",CR        DB "He's evil, I guess...",HERO		endif		if RUS        DB "Что он делает?",SOBES        DB "Колокол ворует... Вот гад! И чары",CR        DB "какие-то напускает на все вокруг.",CR        DB "Ай!",HERO		else	DB "What's he doing?",SOBES        DB "Stealing the bell. What a",CR        DB "bastard! He's putting some kind of",CR	DB "charm on everything.",CR        DB "Ouch!",HERO		endif		if RUS        DB "Что?!",SOBES        DB "Камеру видеонаблюдения разбил,",CR        DB "негодяй!",HERO		else        DB "What?!",SOBES        DB "This bastard broke my camera!",HERO				endif		if RUS        DB "Ну, в общем, все ясно. Надо",CR        DB "наказать нахала... Поможешь мне?",SOBES        DB "А я разве уже не помог?",HERO		else        DB "Well, now it's clear. We should",CR        DB "punish this impudent...",CR	DB "Can you help me?",SOBES        DB "Didn't I help you already?",HERO				endif		if RUS        DB "Эх, молодежь! Ну, прощай...",SOBES        DB "Прощай, Вера! Удачи тебе!",SCRPT,60		else        DB "Eh, youth! Goodbye then...",SOBES        DB "Goodbye, Vera, and good luck!",SCRPT,60		endifGEND        INCLUDE "02ZLOVET.asm"        INCLUDE "03HEVOR.asm"        INCLUDE "04BUKVIN.asm"        INCLUDE "05JORA.asm"        INCLUDE "06PETRO.asm"        INCLUDE "07ALENA.asm"        INCLUDE "08FEKLA.asm"        INCLUDE "09ZADOL.asm"        INCLUDE "0AREPLIC.asm";ObjTab=$        ;ORG ObjTab        ;DB "GLEB    t"        ;DW GL_        ;DW GEND-GL_        ;DB 16        ;DB "xt"        savebin "gleb.txt",GL_,GEND-GL_        ;DB "ZLOVET  t"        ;DW ZL_        ;DW ZLEND-ZL_        ;DB 16        ;DB "xt"        savebin "zlovet.txt",ZL_,ZLEND-ZL_        ;DB "HEVOR   t"        ;DW HV_        ;DW HEND-HV_        ;DB 16        ;DB "xt"        savebin "hevor.txt",HV_,HEND-HV_        ;DB "BUKVIN  t"        ;DW BK_        ;DW BEND-BK_        ;DB 16        ;DB "xt"        savebin "bukvin.txt",BK_,BEND-BK_        ;DB "JORA    t"        ;DW JR_        ;DW JEND-JR_        ;DB 16        ;DB "xt"        savebin "jora.txt",JR_,JEND-JR_        ;DB "PETRO   t"        ;DW PE_        ;DW PEND-PE_        ;DB 16        ;DB "xt"        savebin "petro.txt",PE_,PEND-PE_        ;DB "ALENA   t"        ;DW AL_        ;DW AEND-AL_        ;DB 16        ;DB "xt"        savebin "alena.txt",AL_,AEND-AL_        ;DB "FEKLA   t"        ;DW FK_        ;DW FEND-FK_        ;DB 16        ;DB "xt"        savebin "fekla.txt",FK_,FEND-FK_        ;DB "ZADOLB  t"        ;DW ZD_        ;DW ZEND-ZD_        ;DB 16        ;DB "xt"        savebin "zadolb.txt",ZD_,ZEND-ZD_        ;DB "REPLIC  t"        ;DW REPLIC        ;DW REND-REPLIC        ;DB 16        ;DB "xt"        savebin "replic.txt",REPLIC,REND-REPLIC        ;DB 0 ;END        ;ORG #6100        ;INCLUDE "SAVEOBJ4"        ;ORG #6100        DISPLAY "Gleb=   ",GEND-GL_        DISPLAY "Zlovet= ",ZLEND-ZL_        DISPLAY "Hevor=  ",HEND-HV_        DISPLAY "Bukvin= ",BEND-BK_        DISPLAY "Jora=   ",JEND-JR_        DISPLAY "Petro=  ",PEND-PE_        DISPLAY "Alena=  ",AEND-AL_        DISPLAY "Fekla=  ",FEND-FK_        DISPLAY "Kuznec= ",ZEND-ZD_        DISPLAY "Replic= ",REND-REPLIC        DISPLAY "В сумме=",REND-GL_