;ЗловетZL_SM=-ZL_+PL        DW ZL0+SM,ZL1+SM,ZL2+SM,ZL3+SM        DW ZL4+SM,ZL5+SM,ZL6+SM,ZL7+SM        DW ZL8+SM,ZL9+SM        if RUSZL0     DB "Возьмите, может это поубавит ваш",CR        DB "пыл.",SOBES        DB "Всё что мне нужно - я сам возьму!",CR        DB "Убирайся!",END		elseZL0     DB "Take this and calm down.",SOBES        DB "Anything that I need, I will take",CR        DB "for myself! Get out!",END				endif        if RUSZL1     DB "Зловет, уходи подобру-поздорову!",SOBES        DB "Кто ты, чтобы мне указывать?",HERO		elseZL1     DB "Get away, Evilon, while you can!",SOBES        DB "Who are you to tell me what to do?",HERO				endif        if RUS        DB "Я колдунья Вера, пришла прогнать",CR        DB "тебя, окаянного!",SOBES        DB "Смотри, старуха, договоришься!",CR        DB "Так накажу - век не забудешь!",DIALOG,2		else        DB "I am the sorceress Vera and I am",CR        DB "here to chase you away!",SOBES        DB "Beware, crone! I will punish you",CR        DB "so hard that you will regret it!",DIALOG,2				endif        if RUSZL2     DB "Хорош языком молоть, шалопай! Не",CR        DB "напугаешь!",SOBES        DB "Ну, всё, я предупреждал...",SCRPT,44		elseZL2     DB "Enough talk, ruffian! I am not",CR        DB "afraid!",SOBES        DB "Well... OK. You've been warned.",SCRPT,44				endif         if RUSZL3     DB "И чего люди тебя боятся? Хулиган",CR        DB "он и есть хулиган!",SOBES        DB "Ну и настырная же ты, бабка! Если",CR        DB "ты заговоришь со мной еще раз,",CR        DB "вгоню тебя в землю - шагу ступить"        DB NEXT        DB "не сможешь!",SCRPT,45		elseZL3     DB "Why are people afraid of you?",CR        DB "A bully is always a bully!",SOBES        DB "How dare you?! One more word and I",CR        DB "will pound you into the ground!",SCRPT,45				endif         if RUSZL4     DB "Хорош врать, мальчишка!",SOBES        DB "Ах так! Ну тогда получай!",SCRPT,46		elseZL4     DB "Stop lying to me, boy!",SOBES        DB "Ah so! Well then, watch this!",SCRPT,46				endif         if RUSZL5     DB "Послушай, Зловет, почему ты такой",CR        DB "злой?",SOBES        DB "Убирайся прочь, бабка!",SCRPT,46		elseZL5     DB "Listen to me, Evilon. Why are you",CR        DB "so angry?",SOBES        DB "Get out, crone!",SCRPT,46				endif         if RUSZL6     DB "Злове-е-ет... как твои глазки?",SOBES        DB "Что за голос? Кто здесь смеет со",CR        DB "мной шутить?",END		elseZL6     DB "Evilon... what's with your eyes?",SOBES        DB "Who are you?",CR        DB "Who dares to joke with me?",END				endif         if RUSZL7     DB "На, забери!",SOBES        DB "Что такое? Кто тут?",END		elseZL7     DB "Take it!",SOBES        DB "What? Who are you?",END				endif        if RUSZL8     DB "Ну что, Зловет, допрыгался?",SOBES        DB "Смилуйся, я ни в чем не виноват.",END		elseZL8     DB "Well, Evilon. Are you fine now?",SOBES        DB "Have mercy. I am not guilty!",END				endif        if RUSZL9     DB "Забирай это и убирайся!",SOBES        DB "Отдай мне лучше посох и я обещаю,",CR        DB "что тебе за это ничего не будет.",HERO        DB "Ха, размечтался!",END		elseZL9     DB "Take this and get out!",SOBES        DB "You had better give me the staff",CR        DB "and I promise that you will be OK.",HERO        DB "Hah! You must be dreaming!",END				endif;           123456789-123456789-123456789-123ZLEND