;Старейшина ХеворHV_SM=-HV_+PL        DW H0+SM,H1+SM,H2+SM,H3+SM,H4+SM,H5+SM,H6+SM,H7+SM        DW H8+SM,H9+SM        DW H10+SM,H11+SM,H12+SM,H13+SM,H14+SM,H15+SM,H16+SM        DW H17+SM				if RUS		H0      DB "Вам случайно это не нужно?",SOBES        DB "А что это? Не, не надо... Я бы",CR        DB "попросил...",END		elseH0      DB "Do you need this?",SOBES        DB "What is it? No, I don't need it.",CR        DB "I would ask you if I did...",END				endif		if RUS	H1      DB "Здравствуйте, дедушка.",CR        DB "Расскажите, что у вас тут",CR        DB "случилось?",SOBES        DB "Какой я тебе дедушка, подруга!",CR        DB "Я - Хевор. Старейшина. А ты кто",CR        DB "такая? Чего тут вынюхиваешь?",HERO		elseH1      DB "Hello grandfather.",CR        DB "Tell me, what's happened?",SOBES        DB "I'm no grandfather of yours,",CR        DB "friend! I am Hevor, the elder.",CR        DB "Who are you? What are you sniffing",CR		DB "around here for?",HERO				endif		if RUS	        DB "Не валяйте дурака, Хевор! Это же",CR        DB "я, Вера.",SOBES        DB "Какая Вера? Колдунья? Непохожа!",HERO		else        DB "Don't kid around, Hevor! It's me,",CR        DB "Vera.",SOBES        DB "Vera? The sorceress? You don't",CR		DB "look like her!",HERO				endif;           123456789-123456789-123456789-123		if RUS	        DB "Непохожа?! Ну и сидите тут,",CR        DB "бедствуйте...",SOBES        DB "Постой! Настроение у меня плохое.",CR        DB "К тому же, я вас действительно",CR        DB "не узнаю... Может намекнете чем?",HERO		else        DB "I am not like her?! Goodbye and",CR        DB "have a nice day...",SOBES        DB "Wait! I'm in a bad mood. Besides,",CR        DB "I really don't recognise you.",CR        DB "Can you help me?",HERO				endif		if RUS	        DB "Вам паспорт показать что ли?",SOBES        DB "Валяй.",HERO		else		DB "Do you want to see my ID?",SOBES        DB "That would work.",HERO		endif		if RUS	        DB "Выдавали бы их еще колдуньям...",DIALOG,2		else		DB "When did you ever see a sorceress",CR		DB "who carried ID?",DIALOG,2		endif		if RUS	H2      DB "Мне все еще нужно доказывать, что",CR        DB "я Вера?",SOBES        DB "Непременно!",END		elseH2      DB "Should I still prove my identity?",SOBES        DB "Sure!",END				endif		if RUS	H3      DB "Знакомый почерк?",SOBES        DB "Да, это мое письмо. Так значит,",CR        DB "вы и есть Вера! Простите, давно",CR        DB "вас не видел, поэтому не признал.",HERO		elseH3      DB "Does this handwriting look",CR		DB "familiar?",SOBES        DB "Yes. This is my letter. So, you are",CR        DB "Vera! I am sorry.",CR        DB "It was long ago that I saw you.",HERO				endif		if RUS	        DB "Рассказывайте, что случилось?",SOBES        DB "Кто-то зло на нас держит. Строит",CR        DB "пакости всякие: одни вещи",CR        DB "пропадают, другие появляются,"        DB NEXT        DB "а остальные ведут себя совсем",CR        DB "не так, как обычно. И вообще -",CR        DB "посмотрите вокруг! Даже фонтан"        DB NEXT        DB "пересох...",HERO		else        DB "Tell me. What has happened?",SOBES        DB "Someone is angry and making",CR        DB "mischief. Some things disappear,",CR        DB "others appear,"        DB NEXT        DB "the rest don't look right.",CR        DB "Just take a look around!",CR        DB "Even the fountain is dry...",HERO				endif		if RUS	        DB "Кто ж вас так невзлюбил?",SOBES        DB "Не знаю. Никто его не видел...",CR        DB "Да и вообще, вроде кроме меня",CR        DB "никто ничего плохого не замечает.",HERO		else        DB "Who is responsible? Any ideas?",SOBES        DB "I don't know. Nobody saw...",CR        DB "In any case, no one notices",CR        DB "the problems except for me.",HERO				endif		if RUS	        DB "Может все-таки видел кто? Или",CR        DB "вдруг знает?",SOBES        DB "Может Провидец знает?",HERO		else        DB "Maybe someone saw something or",CR        DB "knows about it?",SOBES        DB "You should ask the Seer.",HERO				endif		if RUS	        DB "Кто такой?",SOBES        DB "Странный человек. Глебом зовут.",CR        DB "Видит разное... Иногда хорошее,",CR        DB "иногда плохое. В общем, странный"        DB NEXT        DB "он... Хотя, вроде, не злой.",HERO		else        DB "Who is that?",SOBES        DB "The strange one. His name is Gleb.",CR        DB "He can see... sometimes good,",CR        DB "sometimes bad. A strange guy..."        DB NEXT        DB "But I don't think he is evil.",HERO				endif		if RUS	        DB "Правда не злой? Он сам не мог",CR        DB "такого натворить?",SOBES        DB "Не знаю... Спроси у него сама.",HERO		else        DB "Are you sure?",CR		DB "Could he be the one who did this?",SOBES        DB "I don't know...",CR		DB "You should ask him.",HERO				endif 		if RUS        DB "А где его найти?",SOBES        DB "Где-то... Я же говорю, странный",CR        DB "он...",HERO		else        DB "How can I find him?",SOBES        DB "He is somewhere... He is",CR        DB "strange... as I said.",HERO				endif		if RUS        DB "Ладно. Пойду поищу.",SCRPT,59		else        DB "Fine. I'll find him on my own.",SCRPT,59				endif		if RUSH4      DB 5,67,"Ну, где этот ваш Глеб?",SOBES        DB "Ох, не знаю...",END		elseH4      DB 5,67,"Where is your Seer?",SOBES        DB "Oh, I don't know...",END				endif		if RUSH5      DB "Глеб говорит, что колдун Зловет",CR        DB "здесь безобразничает... Надо его",CR        DB "найти и побеседовать по душам.",SOBES        DB "Колдун? Ох, и страшно мне!",HERO		elseH5      DB "Gleb told me that Evilon is to",CR        DB "blame for this mess... I must",CR        DB "find him and talk face to face.",SOBES        DB "The sorcerer? I'm frightened!",HERO				endif 		if RUS        DB "Не бойтесь, старейшина! Вы должны",CR        DB "быть примером для всех жителей!",CR        DB "И не таких колдунов в Морию"        DB NEXT        DB "сбрасывали! Пойду искать нахала.",SOBES		else		DB "You must not fear! You must set",CR        DB "an example for the villagers!",CR        DB "I have defeated even more terrible"        DB NEXT        DB "sorcerers! I'll find the bastard.",SOBES		endif		if RUS        DB "Не торопись, уважь сначала",CR        DB "старика. Тоскливо мне очень.",CR        DB "Раньше хоть фонтан журчал -"        DB NEXT        DB "настроение подымал. ",34,"Оживи",34,CR        DB "его, пожалуйста! Вот, у меня",CR        DB "ведро есть...",SCRPT,8		else        DB "Please don't hurry off.",CR        DB "I am very sad. Before, at least",CR        DB "the fountain murmured - and"        DB NEXT        DB "lightened the mood. ",34,"Revive",34,CR        DB "it, please! I have the bucket for",CR        DB "you. Here you are...",SCRPT,8				endif		if RUSH6      DB "Дорогой Хевор, вы не подскажете,",CR        DB "как мне ",34,"оживить",34," фонтан?",SOBES        DB "А вы точно волшебница? Мы его",CR        DB "и безо всякого волшебства",CR        DB "запускали...",END		elseH6      DB "Dear Hevor, could you tell me,",CR        DB "how I can ",34,"revive",34," the fountain?",SOBES        DB "Are you really a sorcerer? We",CR        DB "made it without any magic...",END		endif 		if RUSH7      DB "Слышите? Вода журчит!",SOBES        DB "Благодарю тебя, Вера. Возьми эту",CR        DB "штуку, она притягивает к себе",CR        DB "ложки, вилки и даже ножи! Вещь"        DB NEXT        DB "явно магическая. Боюсь ее у себя",CR        DB "держать...",SCRPT,12		elseH7      DB "Do you hear? The fountain works!",SOBES        DB "Thank you, Vera. Take this thing.",CR        DB "It attracts spoons, forks and even",CR        DB "knives! This is definitely a magic"        DB NEXT        DB "artifact. I'm afraid to keep it.",SCRPT,12		endif		if RUSH8      DB "Как дела, Хевор?",SOBES        DB "Да ничего, помаленьку.",END		elseH8      DB "How are you, Hevor?",SOBES        DB "I am fine. Fine...",END		endif		if RUSH9      DB "Послушайте, Хевор, вы не знаете,",CR        DB "как собрать пшеницу и хмель?",SOBES        DB "Косой! Или серпом... Можно даже",CR        DB "ножиком, если изловчиться...",HERO		elseH9      DB "Listen, Hevor. How can I harvest",CR        DB "wheat and hops?",SOBES        DB "A scythe! Or a sickle... You can",CR        DB "even use a knife, in a pinch.",HERO				endif		if RUS        DB "А вы не подскажете, где найти",CR        DB "что-нибудь острое?",SOBES        DB "Серп-то у меня есть, но я уже",CR        DB "слаб и храню его для защиты. Мне",CR        DB "бы Амулет Здоровья... Я б никого"        DB NEXT        DB "не боялся!",SCRPT,33		else        DB "Could you tell me where I can",CR        DB "find something sharp?",SOBES        DB "I have the sickle, but I am weak",CR        DB "and keeping it for protection. If",CR        DB "only I had the Amulet of Health,"        DB NEXT        DB "I would fear no one!",SCRPT,33				endif		if RUSH10     DB "Как настроение, Хевор?",SOBES        DB "Страшно...",END		elseH10     DB "How are you, Hevor?",SOBES        DB "Nervous...",END				endif		if RUSH11     DB "Я нашла Амулет. Возьмите его,",CR        DB "Хевор.",SOBES        DB "Ой, спасибо! Как и обещал, даю",CR        DB "тебе серп. Только предупреждаю,",CR        DB "он несколько туповат.",SCRPT,15		elseH11     DB "I found the Amulet. Take it,",CR        DB "Hevor.",SOBES        DB "Wow! Thank you! Here is your",CR        DB "sickle, as I promised. I should",CR        DB "warn you, it's a little dull.",SCRPT,15		endif		if RUSH12     DB "Хевор, вы не подскажете, каким",CR        DB "образом можно уничтожить Зловета?",SOBES        DB "Всякая нечисть боится огня! Это я",CR        DB "в одной книжке у Буквина читал.",SCRPT,51		elseH12     DB "Tell me, Hevor.",CR        DB "How I can defeat Evilon?",SOBES        DB "He must be afraid of fire! I read",CR        DB "it in that book of Bookwyn's.",SCRPT,51				endif		if RUSH13     DB "Хевор, вы меня видите?",SOBES        DB "А? Сгинь, нечисть! У меня Амулет",CR        DB "есть!",END		elseH13     DB "Can you see me, Hevor?",SOBES        DB "What? Begone, evil! I have the",CR        DB "Amulet!",END				endif		if RUSH14     DB "Раз у вас нет зубов, возьмите",CR        DB "этот протез, и вы снова сможете",CR        DB "кушать.",SOBES        DB "Ну, Фёкла! Передай ей, что при",CR        DB "встрече я ей уши надеру... И всё",CR        DB "остальное тоже!",SCRPT,43		elseH14     DB "You have no teeth. Take this",CR        DB "prosthesis and you'll able to eat",CR		DB "again.",SOBES        DB "Thecla! Tell her she's getting",CR        DB "a smack next time I see her...",CR        DB "And that's just for starters!",SCRPT,43				endif		if RUSH15     DB "Возьмите, мне не жалко.",SOBES        DB "Меня не напугаешь! Я с Амулетом!",END		elseH15     DB "You can take it. I don't need it.",SOBES        DB "I am not afraid! I have the Amulet!",END				endif		if RUSH16     DB "Хевор, я сожгла Зловета. Что",CR        DB "теперь?",SOBES        DB "Чую, какие-то силы здесь витают.",CR        DB "Ты везде хорошо смотрела?",HERO        DB "Да вроде ничего такого не видала.",SOBES        DB "Ищи лучше, должно быть!",END		elseH16     DB "I burned Evilon. What should I do",CR        DB "now?",SOBES        DB "I can feel some forces here.",CR        DB "Have you checked all around?",HERO        DB "Yes, I have. All is well.",SOBES        DB "You should search harder!",END				endif		if RUSH17     DB "Ну видела я Глеба и что?",SOBES        DB "А ты говорила с ним?",END		elseH17     DB "I found Gleb. What next?",SOBES        DB "Did you talk with him?",END				endifHEND