;БуквинBK_SM=-BK_+PL        DW BK0+SM,BK1+SM,BK2+SM,BK3+SM,BK4+SM,BK5+SM,BK6+SM        DW BK7+SM,BK8+SM		if RUSBK0     DB "Это не вы потеряли?",SOBES        DB "Не, это не моё.",END		elseBK0     DB "Did you lose this?",SOBES        DB "No.",END				endif		if RUSBK1     DB "Здоровечка вам! Меня зовут Вера.",CR        DB "А как вас величают?",SOBES        DB "Я библиотекарь. Буквин моя",CR        DB "фамилия.",HERO		elseBK1     DB "Hi there! My name is Vera",CR        DB "What are you called?",SOBES        DB "I am a librarian. My name is",CR        DB "Bookwyn.",HERO				endif		if RUS        DB "Вы не подскажете, что у вас тут",CR        DB "случилось?",SOBES        DB "Не мешайте, бабуля, учет у меня."        DB DIALOG,3		else        DB "Could you tell me what happened",CR        DB "here?",SOBES        DB "Don't bother me, grandma.",CR		DB "I have to study."        DB DIALOG,3				endif		if RUSBK2     DB "Милок, навостри уши, дело есть.",CR        DB "Задумала тут пивком побаловаться,",CR        DB "а из чего его готовят, забыла..."        DB SOBES		elseBK2     DB "Listen up, boy. I have an idea.",CR        DB "Would be good to drink some beer,",CR        DB "but I forget how to make it..."        DB SOBES				endif		if RUS        DB "Пивко, это дело! Дай, вспомню...",CR        DB "Ага, чтобы пиво зрело, нужен мед,",CR        DB "хмель, зерна пшеницы и... время."        DB NEXT        DB "Тогда добрый напиток выйдет!",SCRPT,25		else		DB "Beer? That's great! Let me think...",CR        DB "Well, you'll need some honey,",CR        DB "hops, ground wheat and... time."        DB NEXT        DB "Then you'll get good drink!",SCRPT,25		endif		if RUSBK3     DB "Что поделываешь, милок?",SOBES        DB "Книги пересчитываю.",END		elseBK3     DB "What are you up to, boy?",SOBES        DB "I'm studying these books.",END		; не совсем уверен, но лучше не подобрал слова... checking - странно звучит		endif		if RUSBK4     DB "Кхе-кхе... Вампиры. Слыхал",CR ;fix by Sergio        DB "о таких? Достали, окаянные. Летають,",CR;BK4     DB "Кхе-кхе... Вампиры. Слыхал о таких?",CR;        DB "Достали, окаянные. Летають,",CR         DB "пугають, вперед не пускають!",SOBES        DB "Да, гады изрядные. Помню, книжка",CR        DB "была, как ентую нечисть изводить.",CR        DB "Ух, и древняя книга...",HERO		elseBK4     DB "Hmmm... Vampires. You know...",CR        DB "They fly around, frighten me,",CR        DB "block my way!",SOBES        DB "Yeah, I know. I have got the",CR        DB "book about them.",CR        DB "The old one...",HERO				endif		if RUS        DB "Почитать бы... Выручай, милок!",SOBES        DB "Я бы с радостью, но правило есть:",CR        DB 34,"Редкие книги на руки выдавать",CR        DB "нельзя",34,". Хотя... Вы слыхали такой"        DB NEXT        DB "научный термин, как ",34,"взятка",34,"?",SCRPT,39		else        DB "I need it... Help me, boy!",SOBES        DB "With pleasure, but my rule is",CR        DB 34,"Don't give away rare books",34,".",CR        DB "But... have you ever heard of"        DB NEXT        DB "the word ",34,"bribe",34,"?",SCRPT,39		endif 		if RUSBK5     DB "Держи, милок! Тебе бы придворным",CR        DB "чиновником служить...",SOBES        DB "Дык был, попёрли... А тут, какой",CR        DB "доход с библиотеки-та?",SCRPT,38		elseBK5     DB "Take this, boy! You should work",CR        DB "as a clerk...",SOBES        DB "Well, I have...  This library",CR        DB "doesn't make any money...",SCRPT,38				endif		if RUSBK6     DB "Чой-то я заплутала...",SOBES        DB "Ну вот, опять мне голову напекло!",END		elseBK6     DB "Oh... I am lost...",SOBES        DB "And I've got sunstroke again!",END		 ;не совсем уверен с термине		endif		if RUSBK7     DB "Посторожи-ка эту штуку...",SOBES        DB "Сгиньте окаянные!",END		elseBK7     DB "Keep this thing...",SOBES        DB "Die, scum!",END				endif;           123456789-123456789-123456789-123		if RUSBK8     DB "Пожалей бабку, отдай книгу.",SOBES        DB "Ты чо, бабуля, сдурела? Что щас",CR        DB "за так делается-то?!",END		elseBK8     DB "Have pity. Give me the book!",SOBES        DB "Are you crazy? There's no",CR        DB "such thing as a free lunch!",END				endifBEND