;Волк ЖораJR_SM=-JR_+PL        DW J0+SM,J1+SM,J2+SM,J3+SM		if RUSJ0      DB "Возьми, авось с голодухи и это",CR        DB "в радость будет.",SOBES        DB "Извини, бабуля, но я таким разом",CR        DB "лучше тебя скушаю.",END		elseJ0      DB "Take this. You'll be glad to",CR        DB "have it if you are hungry.",SOBES        DB "Sorry grandma, but I'd rather",CR        DB "eat you.",END		endif		if RUSJ1      DB "Здравствуй, Жорик!",SOBES        DB "И тебе не болеть, бабуля!",HERO		elseJ1      DB "Hello Eager!",SOBES        DB "Hello granny!",HERO		endif		if RUS        DB "Ты чего тут опять расселся?",SOBES        DB "Да кушать очень хочется. У тебя",CR        DB "случайно нет ничего вкусненького?",HERO		else        DB "Why you are sitting here?",SOBES        DB "I am very hungry.",CR        DB "Do you have something tasty?",HERO				endif		if RUS        DB "Кой-чего есть, но я собралась",CR        DB "в дорогу.",SOBES        DB "Значит, мне придется полакомиться",CR        DB "бабушкой. Зайцы-то совсем не",CR        DB "уродились...",DIALOG,2		else        DB "I have something to eat, but",CR        DB "I must go.",SOBES        DB "Well, then I will eat you.",CR        DB "Not enough hares were born",CR        DB "this year...",DIALOG,2		endif		if RUSJ2      DB "Пропусти меня, я очень тороплюсь!",SOBES        DB "Да я завсегда! Но волчий инстинкт",CR        DB "да пустой желудок погодить мне",CR        DB "велят.",END		elseJ2      DB "Let me go. I am in a hurry!",SOBES        DB "I'd love to, but wolf instinct and",CR        DB "an empty stomach do not allow it.",END		endif		if RUSJ3      DB "Кушай, серый, тебе понравится.",SOBES        DB "Красная Шапочка, конечно,",CR        DB "питательнее, ну да ничего - это",CR        DB "тоже пойдет...",NEXT        DB "Схватив бутерброд, довольный волк",CR        DB "направился в лес.",SCRPT,2		elseJ3      DB "Eat this, wolfy. You'll like this.",SOBES        DB "Red Riding Hood is rather tasty,",CR        DB "but this is not bad either...",NEXT        DB "Grabbing a sandwich, the happy",CR        DB "wolf headed into the forest.",SCRPT,2		endif;           123456789-123456789-123456789-123JEND