;Бармен ПетроPE_SM=-PE_+PL        DW PE0+SM,PE1+SM,PE2+SM,PE3+SM,PE4+SM        DW PE5+SM,PE6+SM,PE7+SM        DW PE8+SM,PE9+SM,PE10+SM,PE11+SM,PE12+SM        DW PE13+SM,PE14+SM        DW PE15+SM,PE16+SM,PE17+SM,PE18+SM		if RUSPE0     DB "Добрые люди сказали, что эта вещь",CR        DB "ваша.",SOBES        DB "Я бы на вашем месте, бабуля, не",CR        DB "верил таким добрым...",END		elsePE0     DB "Nice people said that this thing",CR        DB "belongs to you.",SOBES        DB "If I were you, granny, I wouldn't",CR        DB "believe such nice people...",END				endif		if RUSPE1     DB "Здравствуйте, молодой человек.",CR        DB "Чем это вы занимаетесь?",SOBES        DB "Пивко варю. Людей пою. Да денюжку",CR        DB "собираю.",END		elsePE1     DB "Hello, young man.",CR        DB "What are you doing?",SOBES        DB "Brewing beer, getting people",CR        DB "drunk and earning money.",END				endif		if RUSPE2     DB "Зачем это вы народ честной",CR        DB "спаиваете?",SOBES        DB "А кто вы собственно такая будете,",CR        DB "мадемуазель?",HERO		elsePE2     DB "Why are you getting those good",CR        DB "people drunk?",SOBES        DB "By the way, who are you,",CR        DB "mademoiselle?",HERO				endif		if RUS        DB "Я - колдунья. Звать меня Вера.",CR        DB "Пришла помочь беде вашей.",SOBES        DB "Меня зовут Петро. И никого, между",CR        DB "прочим, я не спаиваю. Сами бегут",CR        DB "ко мне, а я их от жажды спасаю.",HERO		else	DB "I am the sorceress Vera.",CR        DB "I have come to help you.",SOBES        DB "My name is Petro. Since you ask,",CR        DB "I am not getting anybody drunk",CR        DB "by force. I save them from thirst.",HERO		endif		if RUS        DB "Ага, особливо во-о-он того",CR        DB "бедолагу спасли, который стол",CR        DB "лбом вытирает!",SOBES        DB "Дак солнце сегодня шибко жаркое!",CR        DB "Вот голову-то ему и напекло...",HERO		else	DB "Yes, I see. Look at this guy,",CR        DB "wiping the table with his face!",SOBES        DB "Well, it is hot today!",CR        DB "He probably has sunstroke...",HERO		endif		if RUS        DB "Ну, будь по-твоему... А ну-ка и я",CR        DB "приобщусь к пивцу свеженькому!",SOBES        DB "Хм, а вам-то зачем?",HERO		else	DB "Perhaps so. What if I asked for",CR        DB "some freshly brewed beer?",SOBES        DB "Hmm... Why do you need it?",HERO		endif		if RUS        DB "В горле пересохло от пыли",CR        DB "дорожной... Мечи, скорее, кружку!",CR        DB "Да похолоднее!",SOBES        DB "Ох, не могу... Последнее вон тем",CR        DB "двум гаврикам ушло...",HERO		else	DB "My throat is dry from the dusty",CR        DB "road... Stop talking and bring",CR        DB "my cold beer!",SOBES        DB "Oh, I can't... I sold the last",CR        DB "of the beer to those guys...",HERO		endif		if RUS        DB "Что же мне от жажды теперь",CR        DB "помирать?",SOBES        DB "Да я нового вмиг наварю! Только в",CR        DB "амбаре у меня шаром покати. Мне б",CR        DB "того, из чего пиво варят найти...",HERO		else        DB "Shall I die from thirst?",SOBES        DB "I can brew new beer at once!",CR        DB "But I have no ingredients for it",CR        DB "and I can't get any more...",HERO				endif		if RUSPE3     DB "Петро, а из чего пиво варят? Что",CR        DB "вам нужно?",SOBES        DB "Ага! Так я и скажу! Секрет фирмы.",CR        DB "Ну да вы же колдунья, все секреты",CR        DB "знаете!",SCRPT,24		elsePE3     DB "How do you make beer, Petro? What",CR        DB "do you need?",SOBES        DB "Ha! That's my trade secret!",CR        DB "On the other hand, you are the",CR        DB "all-knowing sorceress!",SCRPT,24				endif		if RUSPE4     DB "Вот хмель. Угадала?",SOBES        DB "Смотри-ка!.. А где остальное?",HERO        DB "Скоро принесу.",DIALOG,12		elsePE4     DB "Here are the hops. Right?",SOBES        DB "Wow!... And the rest?",HERO        DB "You'll get it soon.",DIALOG,12				endif		if RUSPE5     DB "Вот вам мед...",SOBES        DB "В точку! Но чего-то не хватает...",CR        DB DIALOG,12		elsePE5     DB "Here is some honey...",SOBES        DB "Perfect! But I am still missing",CR	DB "something...",CR        DB DIALOG,12				endif		if RUSPE6     DB "Возьмите пшеницу.",SOBES        DB "Колосья? Спасибо, веник у меня",CR        DB "уже есть...",DIALOG,12		elsePE6     DB "Take this wheat.",SOBES        DB "Ears of corn? Thanks, but",CR        DB "I already have a broom...",DIALOG,12				endif		if RUSPE7     DB "Вот вам зерна пшеницы.",SOBES        DB "Вот это другое дело! Еще немного",CR        DB "и все...",DIALOG,12		elsePE7     DB "Here are the wheat grains.",SOBES        DB "Good! I've got almost everything",CR        DB "I need now...",DIALOG,12				endif		if RUSPE8     DB "Возьмите последний ингредиент.",CR        DB "Когда будет готово пиво?",SOBES        DB "Приходите через полчаса.",SCRPT,26		elsePE8     DB "Here is the last ingredient.",CR        DB "When do I get my beer?",SOBES        DB "I need half an hour.",SCRPT,26				endif		if RUSPE9     DB "Пиво еще не готово?",SOBES        DB "Наберитесь терпения, бабуля!",END		elsePE9     DB "Is my beer ready yet?",SOBES        DB "Hold your horses, granny!",END		endif		if RUSPE10    DB "Ну где же пиво?",SOBES        DB "Забирайте!",SCRPT,27		elsePE10    DB "Where is my beer?",SOBES        DB "Take it!",SCRPT,27				endif		if RUSPE11    DB "Петро, вы не отдадите мне пустой",CR        DB "флакон? Он мне еще нужен.",SOBES        DB "Конечно, берите.",SCRPT,28		elsePE11    DB "Can you give me the empty flask?",CR        DB "I need it.",SOBES        DB "Sure. Here you are.",SCRPT,28				endif		if RUSPE12    DB "Как дела, Петро?",SOBES        DB "Нормально.",END		elsePE12    DB "How are you, Petro?",SOBES        DB "I'm doing fine.",END				endif		if RUSPE13    DB "Вам случайно мед не нужен?",SOBES        DB "Спасибо, но у меня пока есть.",SCRPT,0		elsePE13    DB "Do you need some honey?",SOBES        DB "No thanks. I have some.",SCRPT,0				endif		if RUSPE14    DB "Послушай, Петро, у тебя случайно",CR        DB "огонька не найдется?",SOBES        DB "Найду, но прежде помоги мне найти",CR        DB "кастрюлю. Придворные Зловета",CR        DB "опять ее у меня утащили.",HERO		elsePE14    DB "Tell me, Petro, do you have a",CR        DB "light?",SOBES        DB "I can find one, but help me find",CR        DB "the saucepan first. It was stolen",CR        DB "again by Evilon's henchmen.",HERO				endif		if RUS        DB "Хорошо, я помогу тебе!",SCRPT,31		else        DB "We have a deal!",SCRPT,31				endif		if RUSPE15    DB "Что-то я не могу найти кастрюлю.",CR        DB "Может, ты мне просто так дашь",CR        DB "огонька?",SOBES        DB "Просто так ничего не бывает.",END		elsePE15    DB "I can't find your saucepan.",CR        DB "Can you give me the light for",CR        DB "free?",SOBES		DB "Nothing is free in this world!",END				endif;           123456789-123456789-123456789-123		if RUSPE16    DB "Вот твоя кастрюля, Петро!",SOBES        DB "Кастрюля? Какая кастрюля? Ах,",CR        DB "эта... Возьми спички, ты вроде их",CR        DB "просила.",SCRPT,32		elsePE16    DB "Here is your saucepan, Petro!",SOBES        DB "My saucepan? Which saucepan? Oh,",CR        DB "that one... Here are the matches",CR        DB "you asked me for.",SCRPT,32				endif		if RUSPE17    DB "Петро, скажите...",SOBES        DB "У меня ничего нет, все кончилось.",END		elsePE17    DB "Could you...",SOBES        DB "I have nothing to say.",END				endif		if RUSPE18    DB "Вроде это тебе.",SOBES        DB "А ну кыш отсюдова!",END		elsePE18    DB "I think this is yours.",SOBES        DB "Go away!",END				endifPEND