;АленушкаAL_SM=-AL_+PL        DW AL0+SM,AL1+SM,AL2+SM,AL3+SM,AL4+SM        DW AL5+SM,AL6+SM,AL7+SM,AL8+SM,AL9+SM        DW AL10+SM,AL11+SM		if RUSAL0     DB "Возьми, наверняка пригодится.",SOBES        DB "Спасибо, мне чужого не нужно.",END		elseAL0     DB "Take this. You'll need it.",SOBES	DB "Thanks, but I don't want it.",END		endif		if RUSAL1     DB "Ты чего плачешь, девица?",SOBES        DB "Как же мне не плакать, если",CR        DB "братец не послушал меня, выпил",CR        DB "воды из копытца и стал козленком.",HERO		elseAL1     DB "Why are you crying, girl?",SOBES        DB "My brother drank water from the",CR        DB "footprint and turned into a goat!",HERO		endif;           123456789-123456789-123456789-123		if RUS        DB "Где-то я про такое уже слыхала...",CR        DB "Видать, твоему горю можно как-то",CR        DB "помочь!",SOBES        DB "Дурная вода была в том копытце...",CR        DB "Я теперь воду в жисть пить",CR        DB "не стану! Заберите этот флакон..."        DB HERO		else        DB "I've heard this story before...",CR        DB "Maybe I can help you somehow.",SOBES        DB "There was bad water in that",CR        DB "footprint... I will never drink",CR        DB "water again! Take this flask..."        DB HERO				endif		if RUS        DB "А на что мне пустой флакон-то?",SOBES        DB "Забирайте, мне он боле не нужен!"        DB SCRPT,4		else        DB "Why do I need an empty flask?",SOBES        DB "I don't need it any more!"        DB SCRPT,4				endif		if RUSAL3     DB "Смотри, может, это вернет тебя",CR        DB "к жизни?",SOBES        DB "Я и так живая, вроде. А вдруг",CR        DB "это моему братцу поможет?",DIALOG,11		elseAL3     DB "Look at this. Maybe this will",CR        DB "make you happy?",SOBES        DB "It does. Perhaps this could",CR        DB "help my brother?",DIALOG,11				endif		if RUSAL4     DB "В следующий раз следи за ним",CR        DB "внимательнее, а воду в колодцах",CR        DB "бери!",SOBES        DB "Обязательно! Вот тебе несколько",CR        DB "монет. Не откажи, прими за",CR        DB "спасение братца.",SCRPT,7		elseAL4     DB "You should be careful next time.",CR        DB "Only take water from the wells!",SOBES        DB "I will! I hope you will accept",CR        DB "these few coins as a thank you.",SCRPT,7		endif		if RUSAL5     DB "Как дела?",SOBES        DB "Отлично!",END		elseAL5     DB "How are you?",SOBES        DB "All good!",END				endif		if RUSAL6     DB "Аленушка, ты ничего не знаешь про",CR        DB "волчьи ягоды?",SOBES        DB "Как не знать? Знаю, конечно! Я их",CR        DB "часто у братца моего отнимаю.",HERO		elseAL6     DB "Alena, do you know anything about",CR        DB "nightshade?",SOBES        DB "Sure I do! I often take it away",CR        DB "from my brother.",HERO				endif		if RUS        DB "И правильно! Колдовские они,",CR        DB "нечего малому с ними забавляться.",CR        DB "А где он их находит?",SOBES        DB "Да он всякую гадость где угодно",CR        DB "достанет! Вот, когда в последний",CR        DB "раз с ними играл, веревку у меня"        DB NEXT        DB "потерял, проказник... Поищи,",CR        DB "бабушка, а?",SCRPT,49		else        DB "That's right! Those berries are",CR        DB "witchcraft. He shouldn't play with",CR        DB "them. Where does he get them?",SOBES        DB "He can find garbage anywhere!",CR        DB "Last time I was playing with him,",CR        DB "he lost our rope..."        DB NEXT        DB "Can you find it, grandma?",SCRPT,49		endif		if RUSAL7     DB "Вот, возьми свою веревку.",SOBES        DB "Спасибо, Вера! А вот тебе волчьи",CR        DB "ягоды.",SCRPT,16		elseAL7     DB "Here you are. Take your rope.",SOBES        DB "Thank you, Vera! Here is some",CR        DB "nightshade for you.",SCRPT,16				endif		if RUSAL8     DB "Аленушка, ну хоть ты поговори со",CR        DB "мной.",SOBES        DB "Ой, чур, меня!",END		elseAL8     DB "Alena, please talk to me.",SOBES        DB "Jesus!",END				endif		if RUSAL9     DB "Алена, я тебе эту штуку дам,",CR        DB "а ты сделай меня видимой!",SOBES        DB "Иванушка, это ты опять химичишь?",CR        DB "Вот сейчас возьму веревку и ты",CR        DB "у меня получишь!",END		elseAL9     DB "Alena, I'll give you this thing",CR        DB "and you will make me visible!",SOBES        DB "Is that you joking again, Ivan?",CR        DB "I'll take my rope and give you",CR        DB "what for!",END				endif		if RUSAL10    DB "Ты все еще печалишься, девица?",SOBES        DB "А что мне остается делать?",HERO        DB "Тебя звать-то хоть как?",SOBES        DB "Аленушкой кличут.",HERO        DB "А меня Верой зовут. Верь мне,",CR        DB "я помогу тебе!",DIALOG,11		elseAL10    DB "Are you still sad, girl?",SOBES        DB "How else should I be?",HERO        DB "What is your name?",SOBES        DB "Alena.",HERO        DB "My name is Vera, and I'll help",CR        DB "you. Trust me!",DIALOG,11				endifAL2		if RUSAL11    DB "Как твои дела, Алена?",SOBES        DB "Ах, оставь меня, я в печали.",CR        DB "Нету жизни мне теперь...",END		elseAL11    DB "How are you, Alena?",SOBES        DB "Oh, let me alone. I am sad.",CR        DB "I cannot go on any more...",END				endifAEND