;ФёклаFK_SM=-FK_+PL        DW FK0+SM,FK1+SM,FK2+SM,FK3+SM,FK4+SM        DW FK5+SM,FK6+SM,FK7+SM,FK8+SM,FK9+SM		if RUSFK0     DB "Возьми, красавица!",SOBES        DB "Спасибо, мне это не нужно.",END		elseFK0     DB "Hi pretty one! Take this.",SOBES        DB "Thanks, but I don't need it.",END				endif		if RUSFK1     DB "Здравствуй, девушка!",SOBES        DB "Здравствуйте, бабушка!",HERO		elseFK1     DB "Hello young lady!",SOBES        DB "Hello granny!",HERO				endif		if RUS        DB "Как тебя зовут, красавица?",SOBES        DB "Я Фёкла Девурдалак, борюсь с",CR        DB "нечистью. Хобби у меня такое. А",CR        DB "как вас зовут?",HERO		else	DB "What is your name, pretty one?",SOBES        DB "I am Thecla De Goule, and I'm",CR        DB "fighting the evil spirits. That's",CR	DB "my hobby. And what is your name?",HERO		endif		if RUS        DB "Меня зовут Вера. И хобби у меня",CR        DB "примерно того же толка.",DIALOG,5		else	DB "My name is Vera and I have",CR        DB "about the same hobby.",DIALOG,5		endif		if RUSFK2     DB "Фёкла, ты не поможешь разобраться",CR        DB "с летучими тварями на кладбище?",SOBES        DB "Летучие? Это, наверное, вампиры.",CR        DB "Они не из наших краев. Мало кто",CR        DB "про них знает... Разве что"        DB NEXT        DB "заморские мудрецы про них что-то",CR        DB "могут сказать. Вот только нету их",CR        DB "тут ни одного...",HERO		elseFK2     DB "Thecla, can you help me with the",CR        DB "flying beasts in the cemetery?",SOBES        DB "Flying? Those must be vampires.",CR        DB "They come from another place.",CR        DB "No one knows about them. The"        DB NEXT        DB "foreign scholars could probably",CR        DB "tell you something, but I've",CR        DB "never seen them around here...",HERO		endif		if RUS        DB "Ха! Чтобы чужими знаниями",CR        DB "пользоваться, мудрецы не нужны.",CR        DB "Знания-то по-разному передаются!",SCRPT,37		else	DB "Ha! You don't need any scholars",CR        DB "to use the knowledge.",CR        DB "There are many ways to acquire",CR	DB "knowledge!",SCRPT,37		endif		if RUSFK3     DB "Держи, вот книга... Только больно",CR        DB "мудрёная. Я думала проще будет...",SOBES        DB "Сейчас почитаем... Наверное здесь",CR        DB "какой-то шифр: ",34,"Тефебефе",CR        DB "пофомофожефет чефеснофок",34,"..."        DB NEXT        DB "Гммм ",34,"Чефеснофок",34,"... Наречие явно",CR        DB "древнеатлантское.",HERO        DB "И что это значит?",SOBES        DB "Это значит - ",34,"чеснок",34,". Не знаю,",CR        DB "причем он здесь? Ну да ладно,",CR        DB "возьми, авось чего придумаешь...",SCRPT,40		elseFK3     DB "Take this book... It is far too",CR        DB "complicated for me...",SOBES        DB "Let's see... it seems to be",CR        DB "in code: ",34,"The garthlic",CR        DB "withll helthp youth",34,"..."        DB NEXT        DB "Hmmm ",34,"Garthlic",34,"... Sounds like",CR        DB "ancient Atlantean.",HERO        DB "And what does it mean?",SOBES        DB "This means ",34,"garlic",34,". I don't know",CR        DB "what for. Well, fine,",CR        DB "take it. Might come in handy.",SCRPT,40				endif		if RUSFK4     DB "Охохох... Что же делать...",SOBES        DB "Ой, у меня после той мудрёной",CR        DB "загадки голова болит...",DIALOG,5		elseFK4     DB "Oh... What should I do...?",SOBES        DB "Ouch, I've got a headache",CR        DB "after this puzzle...",DIALOG,5				endif		if RUSFK5     DB "Как твои дела?",SOBES        DB "Нормально.",END		elseFK5     DB "How are you?",SOBES        DB "I'm fine.",END			endif		if RUSFK6     DB "Ты не знаешь, что такого сделать,",CR        DB "чтобы чары Зловета на меня не",CR        DB "действовали?",SOBES        DB 34,"Глаз не видит, сердце не болит,",CR        DB "рука не тянется",34," - Николай",CR        DB "Степанович Ванхельсинг."        DB NEXT        DB "Был, помнится, большим любителем",CR        DB "волчьих ягод и сушеных тараканов.",CR        DB "Что он с ними делал, не знаю, но"        DB NEXT        DB "за девками был охоч подглядывать.",SCRPT,47		elseFK6     DB "Do you know what I should do to",CR        DB "neutralize Evilon's magic?",SOBES        DB 34,"Eye can't see, heart can't beat,",CR        DB "hand can't take",34," - Arthur",CR        DB "Ignatius Conan Van Helsing."        DB NEXT        DB "He was a big fan of wolf berries",CR        DB "and dried cockroach. I have no idea",CR        DB "what he did with it, but he liked"        DB NEXT        DB "to spy on girls.",SCRPT,47				endif		if RUSFK7     DB "Фёкла, почему со мной никто не",CR        DB "разговаривает?",SOBES        DB "Ванхельсинг? Ты опять за мной",CR        DB "подглядываешь?! А ну пошел прочь!",END		elseFK7     DB "Thecla, why will no one talk to me?",SOBES        DB "Van Helsing? Are you spying on",CR        DB "me again?! Go away!",END				endif;           123456789-123456789-123456789-123		if RUSFK8     DB "Тебе это нужно?",SOBES        DB "Спасибо! Давно хочу себе",CR        DB "накладные ногти справить! Возьми",CR        DB "эти зубы и отдай Хевору, а то ему"        DB NEXT        DB "жевать нечем.",SCRPT,63		elseFK8     DB "Do you need this?",SOBES        DB "Thanks! I was dreaming about",CR        DB "false nails! Take these teeth",CR        DB "and give them to Hevor."        DB NEXT        DB "He cannot chew.",SCRPT,63				endif		if RUSFK9     DB "Фёкла, забери у меня всё, только",CR        DB "помоги мне!",SOBES        DB "Степаныч, поди прочь!",END		elseFK9     DB "Thecla, take everything I have!",CR        DB "Just help me!",SOBES        DB "Ignatius, go away!",END				endifFEND