;КузнецZD_SM=-ZD_+PL        DW ZD0+SM,ZD1+SM,ZD2+SM,ZD3+SM,ZD4+SM        DW ZD5+SM,ZD6+SM,ZD7+SM,ZD8+SM,ZD9+SM        DW ZD10+SM,ZD11+SM,ZD12+SM,ZD13+SM		if RUSZD0     DB "Хочешь, подарю?",SOBES        DB "Чо это вы мне тут суете? У меня",CR        DB "у самого такого добра навалом!",END		elseZD0     DB "Is this any good to you?",SOBES        DB "What are you giving me?",CR        DB "I have plenty of that stuff!",END		endif		if RUSZD1     DB "Ну, вылитый кузнец!",SOBES        DB "Я весь в батю! Он тоже кузнец...",HERO;           123456789-123456789-123456789-123        DB "Хорошая у вас семья!",DIALOG,9		elseZD1     DB "You are a blacksmith!",SOBES        DB "Just like my father before me.",HERO;           123456789-123456789-123456789-123        DB "You have a fine family!",DIALOG,9				endif		if RUSZD2     DB "Здравствуйте, меня зовут Вера.",CR        DB "Я пришла помочь вашей деревне.",SOBES        DB "Моя фамилия Задолбайло. Я кузнец.",CR        DB "Кую в общем... Мне интересно, чем",CR        DB "может нам помочь такая маленькая"        DB NEXT        DB "хилая старушка?",HERO		elseZD2     DB "Hello. My name is Vera.",CR        DB "I come to help your village.",SOBES        DB "My name is Wayland and",CR			; Тут объяснение имени https://ru.wikipedia.org/wiki/%D0%92%D0%B5%D0%BB%D1%83%D0%BD%D0%B4#:~:text=%D0%90%D0%BD%D0%B3%D0%BB%D0%B8%D0%B9%D1%81%D0%BA%D0%B0%D1%8F%20%D1%82%D1%80%D0%B0%D0%B4%D0%B8%D1%86%D0%B8%D1%8F,-%D0%90%D0%BD%D0%B3%D0%BB%D0%B8%D1%87%D0%B0%D0%BD%D0%B5%20%D1%81%D0%B2%D1%8F%D0%B7%D1%8B%D0%B2%D0%B0%D1%8E%D1%82%20%D0%92%D0%B5%D0%BB%D1%83%D0%BD%D0%B4%D0%B0&text=%D0%92%D0%B5%D0%BB%D0%B0%D0%BD%D0%B4%20%E2%80%94%20%D0%B7%D0%BD%D0%B0%D0%BC%D0%B5%D0%BD%D0%B8%D1%82%D1%8B%D0%B9%20%D0%BA%D1%83%D0%B7%D0%BD%D0%B5%D1%86.,%D0%B8%D0%BC%D1%8F%20%D0%92%D0%B5%D0%BB%D0%B0%D0%BD%D0%B4%D0%B0%20%D1%81%D1%82%D0%B0%D0%BD%D0%BE%D0%B2%D0%B8%D0%BB%D0%BE%D1%81%D1%8C%20%D0%B7%D0%BD%D0%B0%D0%BA%D0%BE%D0%BC%20%D0%BA%D0%B0%D1%87%D0%B5%D1%81%D1%82%D0%B2%D0%B0.        DB "I am a blacksmith...",CR        DB "You are so frail, granny."        DB NEXT        DB "How could you help me?",HERO		endif		if RUS        DB "Я не старушка, я колдунья!",SOBES        DB "Чо-то Хевор такое говорил...",CR        DB "Но он, кажется, обмолвился, что",CR        DB "колдунья будет в черном плаще,"        DB NEXT        DB "и из рук молнии пускать будет...",HERO		else        DB "I am not a granny!",CR		DB "I am a sorceress!",SOBES        DB "Hevor told me something...",CR        DB "But he said that the sorceress",CR        DB "would be in a black cloak with"        DB NEXT        DB "lightning in her hands...",HERO				endif        if RUS		DB "Глупые суеверия!",SOBES        DB "Как вы сказали?.. Ну да ладно.",CR        DB "Выходит, вся надежда на вас...",SCRPT,21		else		DB "Foolish superstition!",SOBES        DB "What did you say?... OK, fine.",CR        DB "I hope you're up to the job.",SCRPT,21		endif		if RUSZD3     DB "Как у вас дела, Задолбайло?",SOBES        DB "Кую.",END		elseZD3     DB "How are you, Wayland?",SOBES        DB "I am busy forging.",END		endif				if RUSZD4     DB "Извините, вы не подскажете, как",CR        DB "мне пройти через кладбище?",SOBES        DB "Там прорубаться надо... Топором!",CR        DB "Ух, люблю я это дело, только вот",CR        DB "вурдалаков всяких боюсь.",HERO		elseZD4     DB "Could you please help me?",CR        DB "How I can pass the cemetery?",SOBES        DB "You'd better cut through with an",CR        DB "axe! I'd do it myself, but I'm",CR        DB "afraid of ghouls.",HERO		endif		if RUS        DB "Так может вы дадите мне свой",CR        DB "топор?",SOBES        DB "Топор нужно еще ковать, а у меня",CR        DB "в горле пересохло. Мне бы",CR        DB "заправиться.",HERO		else		DB "Could you give me your axe?",SOBES        DB "The axe needs forging. And",CR        DB "I could really use a drink...",HERO				endif		if RUS        DB "Эх, кузнец! Ладно, что-нибудь",CR        DB "придумаю. А ты пока куй.",SCRPT,23		else		DB "Oh you! Well, I'll sort it out.",CR        DB "Get on with your forging.",SCRPT,23		endif				if RUSZD5     DB "Топор еще не готов?",SOBES        DB "Работа спорится... Но сухо...",END		elseZD5     DB "Is the axe ready?",SOBES        DB "Getting there, but I wanna drink!",END		endif		if RUSZD6     DB "Пивко поможет творческому",CR        DB "процессу?",SOBES        DB "Ох, да! И творческому, и",CR        DB "процессу... и... буль-буль...",CR        DB "Эххх... Ах да! Вот атличнейший"        DB NEXT        DB "тапор! Лови... В смысле держи!",SCRPT,22		elseZD6     DB "Maybe some beer will speed up",CR        DB "the process?",SOBES        DB "Oh yeah! It will... process...",CR        DB "and... gurgle...",CR        DB "Ahhh... Good! Here is the axe!"        DB NEXT        DB "Catch! I mean, take it!",SCRPT,22		endifZD7     ;DB "Ну, как пиво? Пенное?",SOBES        ;DB "Спасибо, как раз для моей морды!",CR        ;DB "Петро, как всегда, держит марку.",DIALOG,8		if RUSZD8     DB "Ты не знаешь как расправиться",CR        DB "со Зловетом?",SOBES        DB "Да я его! Я знаишь какой? Да я",CR        DB "дзесяток Злавретов адной левай",CR        DB "ложу...",DIALOG,3		elseZD8     DB "Do you know how to defeat",CR        DB "Evilon?",SOBES        DB "Do you know who I am? I can",CR        DB "kill 10 Evilons with my left",CR        DB "hand...",DIALOG,3		endif		if RUSZD9     DB "Как дела, кузнец?",SOBES        DB "Кую... И пью... Когда я пью,",CR        DB "я кую. Я кую - значит, я пью...",CR        DB "Философия, понимаишь!",END		elseZD9     DB "How's it going, smith?",SOBES        DB "Forging... drinking... I am",CR        DB "forging, when I am drinking.",CR		DB "To forge is to drink...",CR        DB "That's philosophy!",END				endif		if RUSZD10    DB "Привет! У тебя случайно тараканы",CR        DB "не водятся?",SOBES        DB "Черти? Навалом! Вон один как раз",CR        DB "на завалинке щас сидит.",HERO		elseZD10    DB "Hi! Do you have any cockroaches?",SOBES        DB "Imps? Plenty! Look. One of them",CR        DB "is sitting on the mound.",HERO		endif		if RUS        DB "Да не черти! Тараканы... Такие",CR        DB "рыжие, с усами!",SOBES        DB "А, закусь!.. Щазз поймаю... А ты",CR        DB "пока мне курева найди, ладно?",CR        DB "Чертей вроде дымом можно пугнуть!",SCRPT,61		else		DB "Not imps! Cockroaches...",CR        DB "You know, the insects!",SOBES        DB "Snacks?... I'll catch one...",CR        DB "Find something to smoke. OK?",CR        DB "Imps are afraid of smoke!",SCRPT,61		endif;           123456789-123456789-123456789-123		if RUSZD11    DB "Вот тебе курево.",SOBES        DB "Спасибо! А я засушил тараканчика.",CR        DB "Держи.",SCRPT,48		elseZD11    DB "Here is your smoke.",SOBES        DB "Thanks! I dried the cockroach.",CR        DB "Take it.",SCRPT,48		endif		if RUSZD12    DB "Задолбайло, помогите мне!",SOBES        DB "Тьфу, тьфу, нечистая! Сгинь!",END		elseZD12    DB "Smith, help me!",SOBES        DB "Fie, fie, unseen one! Go away!",END		endif		if RUSZD13    DB "Возьмите, кузнец!",SOBES        DB "Ну так и знал, что они вернутся!",END		elseZD13    DB "Take this, blacksmith!",SOBES        DB "I knew for sure that they",CR		DB "would return!",END				endifZEND